import React, { useEffect, useState } from "react";

function UsersList(props) {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [count, setCount] = useState(0);
  const [like, setLike] = useState (0);
  const [name, setName] = useState();

    const handleInputChange = (event) => {
    setName(event.target.value);
  };



  

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((res) => res.json())
      .then((data) => {
        setUsers(data);
        setLoading(false);
      })
        // Add artificial delay before setting loading to false
        setTimeout(() => {
          setLoading(false);
        }, 2000);
    }) //  Empty dependency array to run only once

  if (loading) return <div className="h-full w-full bg-slate-600 text-center p-4 block text-red-400">Loading users...</div>;

  return (
    <div className="container h-full px-8 mt-20">
      <h2 className="block antialiased font-sans text-blue-800 mb-2 text-3xl font-bold">
       {props.name} User List
      </h2>
      
      <div className="grid xl:grid-cols-5 md:grid-cols- mb-10 container gap-5 mx-auto items-start">
        
       {users.map((user) => (
       /* {[...users].reverse().map((user) => ( */
      <div className="relative flex flex-col bg-clip-border rounded-xl bg-white text-gray-700 shadow-md p-4">
        <strong> {user.id}</strong>

 <p className="block antialiased font-sans text-dark-800 mb-2 text-1xl font-bold">
    
    {user.name}
    
    </p>
                <p className="block antialiased font-sans text-sm font-normal leading-relaxed text-inherit !text-gray-500">    {user.email}</p>
                <p className="block antialiased font-sans text-sm font-normal leading-relaxed text-inherit !text-gray-500">    {user.phone}</p>
      </div>
      ))}
      </div>

      <div className="mb-5">
        <h2 className="text-xl mb-2">Count: {count}</h2>
        <div className="">
           <button
          className="text-white bg-green-400 px-4 py-2 rounded mb-2 mr-4"
          onClick={() => setCount(count + 1)}
        >
          Add Number
        </button>
         <button
          className="text-white bg-red-600 px-4 py-2 rounded mb-2"
          onClick={() => setCount(count - 1)}
        >
          Decease No
        </button>
        </div>
       
        <button
          className="text-blue-600 bg-gray-200 px-4 py-2 rounded"
          onClick={() => setCount(0)} //  Clear count here
        >
          Clear Number
        </button>
        <div className="mb-5">
        <h3 className="block antialiased font-sans text-blue-800 mb-2 text-3xl font-bold">LikeDislike <strong>{like}</strong></h3>
        <button className="bg-green-400 p-4 text-white mr-3 rounded" onClick={() => setLike(like + 1)}>Like + </button>
        <input type="" value="DisLike - " className="bg-red-400 p-4 text-white rounded" onChange={() => setLike(like - 1)} />

      </div>
      </div>
     <div className="mb-5">
        <h1 className="block antialiased font-sans text-blue-800 mb-2 text-3xl font-bold">{name}</h1>
        <input type="text" className="block mb-4 border px-4 py-2 w-80" placeholder="Type Your name" value={name} onChange={(handleInputChange)} />
     </div>
    </div>
  );
}

export default UsersList;
