import React, { useEffect, useState } from "react";

function UsersList(props) {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [count, setCount] = useState(0);
  const [like, setLike] = useState(0);
  const [name, setName] = useState("");

  const handleInputChange = (event) => {
    setName(event.target.value);
  };

  useEffect(() => {
    // Simulate a delayed API call
    const fetchData = async () => {
      try {
        const res = await fetch("https://jsonplaceholder.typicode.com/users");
        const data = await res.json();
        setUsers(data);

        // Add artificial delay before setting loading to false
        setTimeout(() => {
          setLoading(false);
        }, 2000); // 2 seconds delay
      } catch (err) {
        console.error("Failed to fetch users:", err);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-slate-600 text-white text-2xl font-bold">
        Loading users...
      </div>
    );
  }

  return (
    <div className="container h-full px-8 mt-20">
      <h2 className="text-blue-800 mb-2 text-3xl font-bold">
        {props.name} User List
      </h2>

      <div className="grid xl:grid-cols-5 gap-5 mb-10">
        {[...users].reverse().map((user) => (
          <div
            key={user.id}
            className="bg-white text-gray-700 shadow-md p-4 rounded-xl"
          >
            <strong>{user.id}</strong>
            <p className="text-xl font-bold">{user.name}</p>
            <p className="text-sm text-gray-500">{user.email}</p>
            <p className="text-sm text-gray-500">{user.phone}</p>
          </div>
        ))}
      </div>

      {/* Count section */}
      <div className="mb-5">
        <h2 className="text-xl mb-2">Count: {count}</h2>
        <button
          className="bg-green-400 px-4 py-2 rounded text-white mr-4"
          onClick={() => setCount(count + 1)}
        >
          Add Number
        </button>
        <button
          className="bg-red-600 px-4 py-2 rounded text-white mr-4"
          onClick={() => setCount(count - 1)}
        >
          Decrease No
        </button>
        <button
          className="bg-gray-200 px-4 py-2 rounded text-blue-600"
          onClick={() => setCount(0)}
        >
          Clear Number
        </button>
      </div>

      {/* Like/Dislike section */}
      <div className="mb-5">
        <h3 className="text-3xl font-bold text-blue-800">
          LikeDislike <strong>{like}</strong>
        </h3>
        <button
          className="bg-green-400 p-4 text-white mr-3 rounded"
          onClick={() => setLike(like + 1)}
        >
          Like +
        </button>
        <button
          className="bg-red-400 p-4 text-white rounded"
          onClick={() => setLike(like - 1)}
        >
          Dislike -
        </button>
      </div>

      {/* Name Input */}
      <div className="mb-5">
        <h1 className="text-3xl font-bold text-blue-800">{name}</h1>
        <input
          type="text"
          className="border px-4 py-2 w-80"
          placeholder="Type Your Name"
          value={name}
          onChange={handleInputChange}
        />
      </div>
    </div>
  );
}

export default UsersList;
