import React, { useEffect, useState } from "react";

function UsersList() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [count, setCount] = useState(0);

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((res) => res.json())
      .then((data) => {
        setUsers(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Failed to fetch users:", err);
        setLoading(false);
      });
  }, []); // ✅ Empty dependency array to run only once

  if (loading) return <p>Loading users...</p>;

  return (
    <div className="container h-full mt-14">
      
      
      <div className="grid xl:grid-cols-2 md:grid-cols-1 container gap-20 mx-auto items-start">
        <h2 className="block antialiased font-sans text-blue-800 mb-2 text-3xl font-bold">
        User List
      </h2>
       {users.map((user) => (
      <div>
        <strong>{user.id}</strong>

 <p className="block antialiased font-sans text-blue-800 mb-2 text-3xl font-bold">
    
    {user.name}
    
    </p>
                <p className="block antialiased font-sans text-xl font-normal leading-relaxed text-inherit !text-gray-500">    {user.email}</p>
                <p className="block antialiased font-sans text-xl font-normal leading-relaxed text-inherit !text-gray-500">    {user.email}</p>
      </div>
      ))}
      </div>
      {/* <ul className="p-5">
        {users.map((user) => (
          <li key={user.id}>
            <br />
            <strong>{user.name}</strong>
            <br /> {user.email}
            <br />
            {user.phone}
          </li>
        ))}
      </ul> */}

      <div className="flex flex-col items-center">
        <h2 className="text-xl mb-2">Count: {count}</h2>
        <button
          className="text-white bg-blue-600 px-4 py-2 rounded mb-2"
          onClick={() => setCount(count + 1)}
        >
          Add Number
        </button>
        <button
          className="text-blue-600 bg-gray-200 px-4 py-2 rounded"
          onClick={() => setCount(0)} // ✅ Clear count here
        >
          Clear Number
        </button>
      </div>
    </div>
  );
}

export default UsersList;
